import React, { useState } from 'react';
import history from 'redux/history';
import { makeStyles, Typography, TextField, Button, Grid, Chip, Paper } from '@material-ui/core';

import CONFIG from 'config';
import { loginUser as loginUserUtil } from 'utils/loginUtil/loginUtil';

const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    maxWidth: 500,
    textAlign: 'center',
    marginTop: '100px'
  },
  image: {
    backgroundImage: 'url(https://source.unsplash.com/random)',
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    margin: theme.spacing(8, 4),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
}));

const Login = () => {
  const classes = useStyles();
  const [loginEmail, setLoginEmail] = useState('');
  const [loginPassword, setLoginPassword] = useState('');
  const [notAllowed, setNotAllowed] = useState('');

  const loginUser = async () => {
    const resp = await loginUserUtil(loginEmail, loginPassword);
    if (resp) {
      setLoginEmail('');
      setLoginPassword('');
      history.push(CONFIG.UI_URL.LANDING);
    } else {
      console.error('NOT ALLOWED');
      setNotAllowed('Invalid login');
    }
  };

  const pressedEnter = (event) => {
    if (event.keyCode === 13) {
      loginUser();
    }
  };

  const handleEmailChange = (event) => {
    setNotAllowed('');
    setLoginEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setNotAllowed('');
    setLoginPassword(event.target.value);
  };

  const handleCloseChip = () => {
    setNotAllowed('');
  };

  return (
    <Grid container className={classes.root}>
      <Grid item xs={12}>
        <Typography variant="h2">Sign In</Typography>
      </Grid>
      {
        notAllowed !== ''
        && (
          <Grid item xs={12}>
            <Chip
              label={notAllowed}
              onDelete={handleCloseChip}
              color="secondary"
            />
          </Grid>
        )
      }
      <Grid item xs={false} sm={4} md={7} className={classes.image} />
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
        <TextField
          id="login-email"
          label="Email *"
          margin="normal"
          value={loginEmail}
          onChange={handleEmailChange}
          onKeyDown={pressedEnter}
          fullWidth
          autoFocus
          autoComplete="email"
        />
        <TextField
          id="login-password"
          label="Password *"
          margin="normal"
          type="password"
          autoComplete="current-password"
          value={loginPassword}
          onChange={handlePasswordChange}
          onKeyDown={pressedEnter}
          fullWidth
          autoComplete="current-password"
        />
        <Button
          variant="contained"
          color="primary"
          size="large"
          onClick={loginUser}
          fullWidth
        >
          Login
        </Button>
      </Grid>
    </Grid>
  );
};

export default Login;
