import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import { Divider, Drawer, Link, List, ListItem, ListItemIcon, ListItemText, Typography } from '@material-ui/core';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';

const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
});

const TopNavMenu = ({ open, closeMenu, onClose }) => {
  const classes = useStyles();

  const copyright = () => {
    return (
      <Typography variant="body2" color="textSecondary" align="center">
        {'Copyright &copy; '}
        <Link color="inherit" href="https://davidlozzi.com/dansdiet">
          David Lozzi
        </Link>
        {` ${new Date().getFullYear()}.`}
      </Typography>
    );
  };


  const sideList = () => (
    <div
      className={classes.list}
      role="presentation"
      onClick={closeMenu}
      onKeyDown={closeMenu}
    >
      <List>
        {['My Diet', 'Share Diet', 'View Diet'].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
      <Divider />
      <List>
        {['About', 'Support'].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
      {copyright}
    </div>
  );

  return (
    <Drawer open={open} onClose={onClose}>
      {sideList('left')}
    </Drawer>
  );
};

TopNavMenu.propTypes = {
  open: PropTypes.bool.isRequired,
  closeMenu: PropTypes.func.isRequired,
  onClose: PropTypes.func
};

TopNavMenu.defaultProps = {
  onClose: () => { }
};

export default TopNavMenu;
