import { createMuiTheme, responsiveFontSizes } from '@material-ui/core/styles';

let myTheme = createMuiTheme({
  overrides: {
    MuiTypography: {
      h1: {
        fontSize: '3rem'
      }
    }
  }
});

myTheme = responsiveFontSizes(myTheme);

export default myTheme;
