import { connectRouter, routerMiddleware } from 'connected-react-router';
import { combineReducers, applyMiddleware, createStore, compose } from 'redux';
import { createLogger } from 'redux-logger';
import thunkMiddleware from 'redux-thunk';

import history from 'redux/history';

import { name as searchReducerName, reducer as searchReducer } from 'redux/api/search/search';
import { name as typeAheadReducerName, reducer as typeAheadReducer } from 'redux/api/type-ahead/type-ahead';

const middleware = [ // Order dependent
  thunkMiddleware, // Enables actions to return functions
  routerMiddleware(history) // Enables dispatching actions
];
if (process.env.NODE_ENV !== 'production') {
  middleware.push(createLogger({ collapsed: true })); // Must be at bottom
}

const store = createStore(
  connectRouter(history)( // Adds router key-value pair to store state
    combineReducers({
      [searchReducerName]: searchReducer,
      [typeAheadReducerName]: typeAheadReducer
    })
  ),
  compose(applyMiddleware(...middleware))
);

export default store;
