import React, { useState } from 'react';
import history from 'redux/history';
import { makeStyles, Typography, TextField, Button, Grid, Chip } from '@material-ui/core';

import CONFIG from 'config';
import { loginUser as loginUserUtil } from 'utils/loginUtil/loginUtil';

const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    maxWidth: 500,
    textAlign: 'center',
    backgroundColor: theme.background
  },
  h2: {
    marginTop: '100px'
  }
}));

const Login = () => {
  const classes = useStyles();
  const [loginEmail, setLoginEmail] = useState('');
  const [loginPassword, setLoginPassword] = useState('');
  const [notAllowed, setNotAllowed] = useState('');

  const loginUser = async () => {
    const resp = await loginUserUtil(loginEmail, loginPassword);
    if (resp) {
      setLoginEmail('');
      setLoginPassword('');
      history.push(CONFIG.UI_URL.LANDING);
    } else {
      console.error('NOT ALLOWED');
      setNotAllowed('Invalid login');
    }
  };

  const pressedEnter = (event) => {
    if (event.keyCode === 13) {
      loginUser();
    }
  };

  const handleEmailChange = (event) => {
    setNotAllowed('');
    setLoginEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setNotAllowed('');
    setLoginPassword(event.target.value);
  };

  const handleCloseChip = () => {
    setNotAllowed('');
  };

  return (
    <Grid container className={classes.root}>
      <Grid item xs={12}>
        <Typography variant="h2" className={classes.h2}>Login</Typography>
      </Grid>
      {
        notAllowed !== ''
        && (
          <Grid item xs={12}>
            <Chip
              label={notAllowed}
              onDelete={handleCloseChip}
              color="secondary"
            />
          </Grid>
        )
      }
      <Grid item xs={12}>
        <TextField
          id="login-email"
          label="Email"
          margin="normal"
          value={loginEmail}
          onChange={handleEmailChange}
          onKeyDown={pressedEnter}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          id="login-password"
          label="Password"
          type="password"
          margin="normal"
          autoComplete="current-password"
          value={loginPassword}
          onChange={handlePasswordChange}
          onKeyDown={pressedEnter}
        />
      </Grid>
      <Grid item xs={12}>
        <Button
          variant="contained"
          color="primary"
          size="large"
          margin="normal"
          onClick={loginUser}
        >
          Login
        </Button>
      </Grid>
    </Grid>
  );
};

export default Login;
