import { createMuiTheme, responsiveFontSizes } from '@material-ui/core/styles';

const myTheme = createMuiTheme({
  overrides: {
    MuiButtonBase: {
      root: {
        marginTop: '1rem'
      }
    },
    MuiTextField: {
      root: {
        marginTop: '1rem'
      }
    },
    MuiTypography: {
      h1: {
        fontSize: '2.5rem'
      },
      h2: {
        fontSize: '2rem'
      }
    }
  }
});

const myThemeResponsive = responsiveFontSizes(myTheme);

export default myThemeResponsive;
